  location /blog/ {
    proxy_pass http://127.0.0.1:8000/;
  }

  location /docs/ {
    proxy_pass http://127.0.0.1:9000/;
  }
}

domain blog.hiproxy.org {
  location / {
    proxy_pass http://127.0.0.1:8000/;
  }
}

